CREATE VIEW [dbo].[Percent_CashLog_FullVersionZZZ]
AS
SELECT        DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) AS Monthdiff, SUBSTRING(dbo.DOCINFO.DI_REF, 2, 1) AS BranCh, dbo.DOCINFO.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
                         dbo.SKUMASTER.SKU_NAME AS itemname, CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '337' THEN (dbo.TRANSTKD.TRD_UTQQTY * dbo.TRANSTKD.TRD_QTY)
                         * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '302' THEN (dbo.TRANSTKD.TRD_UTQQTY * dbo.TRANSTKD.TRD_QTY) END AS amt, dbo.DOCINFO.DI_REF AS Docno
FROM            dbo.SKUMASTER INNER JOIN
                         dbo. ODSMASTER ON dbo.SKUMASTER.SKU_KEY = dbo. ODSMASTER. ODS_SKU INNER JOIN
                         dbo.TRANSTKD INNER JOIN
                         dbo.TRANSTKH ON dbo.TRANSTKD.TRD_TRH = dbo.TRANSTKH.TRH_KEY INNER JOIN
                         dbo.DOCINFO ON dbo.TRANSTKH.TRH_DI = dbo.DOCINFO.DI_KEY INNER JOIN
                         dbo.DOCTYPE ON dbo.DOCINFO.DI_DT = dbo.DOCTYPE.DT_KEY ON dbo. ODSMASTER. ODS_KEY = dbo.TRANSTKD.TRD_ ODS
WHERE        (dbo.DOCINFO.DI_ACTIVE = 0) AND (DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) < 7)
GROUP BY dbo.DOCINFO.DI_DATE, dbo.DOCINFO.DI_REF, dbo.TRANSTKD.TRD_QTY, dbo.TRANSTKD.TRD_N_AMT, dbo.TRANSTKD.TRD_SH_NAME, SUBSTRING(dbo.DOCINFO.DI_REF, 3, 2), SUBSTRING(dbo.DOCINFO.DI_REF, 2, 1),
                         dbo.DOCINFO.DI_ACTIVE, dbo.TRANSTKD.TRD_UTQNAME, dbo.SKUMASTER.SKU_CODE, dbo.SKUMASTER.SKU_NAME, dbo.DOCTYPE.DT_PROPERTIES, dbo.TRANSTKD.TRD_UTQQTY
HAVING        (dbo.DOCTYPE.DT_PROPERTIES = '302') OR
                         (dbo.DOCTYPE.DT_PROPERTIES = '337')

