-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [dbo].[Bplus_Pr]

as
BEGIN
	select Q1.DI_REF as เลขที่ในขอซื้อ,
		   Q1.DI_DATE as วันที่ขอซื้อ,
		   Q1.DI_REMARK as ชื่อผู้ขอซื้อ,
		   Q1.TRD_SH_CODE as รหัสสินค้า,
		   Q1.TRD_SH_NAME as ชื่อสินค้า,
		   Q1.DT_BRANCH as สาขา,
		   Q2.DI_REF as เลขที่ใบสั่งซื้อ,
		   Q2.DI_DATE as วันที่สั่งซื้อ,
		   Q2.DI_CRE_BY as สั่งซื้อโดย,
		   case
		   when Q2.DI_REF is not null then 'ok'
		   when Q2.DI_REF is null then 'ยังไม่มีการสั่งซื้อ'
		   end as สถาณะการสั่งซื้อ
		   from
		((select di_key,di_ref,DI_DATE,DI_REMARK,TRH_LSTATUS,TRD_SH_CODE,TRD_SH_NAME,DT_BRANCH
			from doctype inner join DOCINFO
			on dt_key=di_dt inner join transtkh
			on di_key= trh_di inner join transtkd
			on trh_key=trd_trh
			where DT_PROPERTIES=201 and di_date=convert(nvarchar(20),getdate(),101) and DI_ACTIVE=0) Q1
		full outer join
		(select TRD_REFER_DI,di_key,di_ref,DI_DATE,DI_CRE_BY,TRD_SH_CODE,TRD_SH_NAME
			from doctype inner join DOCINFO
			on dt_key=di_dt inner join transtkh
			on di_key= trh_di inner join transtkd
			on trh_key=trd_trh
			where TRD_REFER_DI in (select di_key from docinfo) and DT_PROPERTIES=203 and DI_ACTIVE=0) Q2
			on Q1.di_key = Q2.TRD_REFER_DI and Q1.TRD_SH_CODE=Q2.TRD_SH_CODE )
		where Q1.di_key !=0 order by Q1.DI_REF,Q1.DT_BRANCH
END

