CREATE PROCEDURE [dbo].[Chandra_VB_ChandraReport]
	--@V int
--
AS
BEGIN
SET NOCOUNT ON;
SET ANSI_WARNINGS OFF
	DECLARE
			@code nvarchar(10),
			@name nvarchar(150),
			@Pr nvarchar(24),
			@Pack nvarchar(15),
			@Dep nvarchar(2),
			@V int



DECLARE it CURSOR FOR
select Max( Report_V)
from Chandra_Report

OPEN it FETCH it INTO
			@V

CLOSE it
DEALLOCATE it


	declare @UItemB table (Pr nvarchar(10),itemname nvarchar(150),itemcode nvarchar(10),pack nvarchar(15))
	insert @UItemB
		select Pr,Name,code,Pack
		from Chandra_report
		where Report_V = @V
		group by Pr,Name,code,Pack



--=====Prodecure Chandra_F_Cash6month=======================================================================
		declare @Cash table (Monthdiff int,BranCh nvarchar(2),DI_ACTIVE smallint,itemcode nvarchar(10),itemname nvarchar(150),Docno nvarchar(24),amt int)
		declare @Cash6m table (Monthdiff int,BranCh nvarchar(2),itemcode nvarchar(10),Sumqtycal int)

		insert into @Cash
		SELECT DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) AS Monthdiff, SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) AS BranCh, dbo.DOCINFO.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
							dbo.SKUMASTER.SKU_NAME AS itemname, dbo.DOCINFO.DI_REF AS Docno, CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '337' THEN (skumove.skm_Qty) * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '302' THEN (skumove.skm_Qty)
							* - 1 END AS amt
		FROM     dbo.DOCTYPE INNER JOIN
							dbo.DOCINFO ON dbo.DOCTYPE.DT_KEY = dbo.DOCINFO.DI_DT INNER JOIN
							dbo.TRANSTKH ON dbo.DOCINFO.DI_KEY = dbo.TRANSTKH.TRH_DI INNER JOIN
							dbo.SKUMOVE ON dbo.DOCINFO.DI_KEY = dbo.SKUMOVE.SKM_DI INNER JOIN
							dbo.SKUMASTER ON dbo.SKUMOVE.SKM_SKU = dbo.SKUMASTER.SKU_KEY
		WHERE  DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE())<6 and (dbo.DOCINFO.DI_ACTIVE = '0') And dbo.DOCINFO.DI_REF  not like 'P%' AND (dbo.DOCTYPE.DT_PROPERTIES IN ('302', '337')) AND (dbo.SKUMASTER.SKU_KEY IN
								(SELECT SKUMASTER_1.SKU_KEY
								FROM      dbo.DOCINFO AS DOCINFO_1 INNER JOIN
													dbo.TRANSTKH AS TRANSTKH_1 ON DOCINFO_1.DI_KEY = TRANSTKH_1.TRH_DI INNER JOIN
													dbo.TRANSTKD ON TRANSTKH_1.TRH_KEY = dbo.TRANSTKD.TRD_TRH INNER JOIN
													dbo.SKUMASTER AS SKUMASTER_1 ON dbo.TRANSTKD.TRD_SKU = SKUMASTER_1.SKU_KEY))
		insert into @Cash6m
		select c.Monthdiff,case when branch = 7 then 1 else branch end as branch,c.itemcode,SUM(c.Amt) as Sumqtycal
		from @cash as c
		where c.Monthdiff <6
		group by c.itemcode,c.Monthdiff,branch

		--=====================================================

		UPDATE Chandra_Report set LastSoldM1cash = sumQtycal
		from @Cash6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 0 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM2cash = sumQtycal
		from @Cash6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch= dst.Br
		where dst.code <>''  and c.Monthdiff = 1 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM3cash = sumQtycal
		from @Cash6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 2 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM4cash = sumQtycal
		from @Cash6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 3 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM5cash = sumQtycal
		from @Cash6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 4 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM6cash = sumQtycal
		from @Cash6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 5 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)
--=====End of Chandra_F_Cash6month ===================================================================================
--=====Proceddure Chandra_F_Credit6month==============================================================================
		declare @Credit table (Monthdiff int,BranCh nvarchar(2),DI_ACTIVE smallint,itemcode nvarchar(10),itemname nvarchar(150),Docno nvarchar(24),amt int)
		declare @Credit6m table (Monthdiff int,BranCh nvarchar(2),itemcode nvarchar(10),Sumqtycal int)

		insert into @Credit
		SELECT DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) AS Monthdiff, SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) AS BranCh, dbo.DOCINFO.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
						  dbo.SKUMASTER.SKU_NAME AS itemname, dbo.DOCINFO.DI_REF AS Docno, CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '307' THEN (skumove.skm_Qty) * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '308' THEN (skumove.skm_Qty)
						  * - 1 END AS amt
		FROM     dbo.DOCTYPE INNER JOIN
						  dbo.DOCINFO ON dbo.DOCTYPE.DT_KEY = dbo.DOCINFO.DI_DT INNER JOIN
						  dbo.TRANSTKH ON dbo.DOCINFO.DI_KEY = dbo.TRANSTKH.TRH_DI INNER JOIN
						  dbo.SKUMOVE ON dbo.DOCINFO.DI_KEY = dbo.SKUMOVE.SKM_DI INNER JOIN
						  dbo.SKUMASTER ON dbo.SKUMOVE.SKM_SKU = dbo.SKUMASTER.SKU_KEY
		WHERE  DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) <6  and (dbo.DOCINFO.DI_ACTIVE = '0') AND SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) is not null AND (dbo.DOCTYPE.DT_PROPERTIES IN ('307', '308')) AND (dbo.SKUMASTER.SKU_KEY IN
							  (SELECT SKUMASTER_1.SKU_KEY
							   FROM      dbo.DOCINFO AS DOCINFO_1 INNER JOIN
												 dbo.TRANSTKH AS TRANSTKH_1 ON DOCINFO_1.DI_KEY = TRANSTKH_1.TRH_DI INNER JOIN
												 dbo.TRANSTKD ON TRANSTKH_1.TRH_KEY = dbo.TRANSTKD.TRD_TRH INNER JOIN
												 dbo.SKUMASTER AS SKUMASTER_1 ON dbo.TRANSTKD.TRD_SKU = SKUMASTER_1.SKU_KEY))
		insert into @Credit6m
		select c.Monthdiff,case when branch = 7 then 1 else branch end as branch,c.itemcode,SUM(c.Amt) as Sumqtycal
		from @Credit as c
		where c.Monthdiff <6 --and c.itemcode in (select code from @UItemB)
		group by c.itemcode,c.Monthdiff,branch

		--=====================================================

		UPDATE Chandra_Report set LastSoldM1credit = sumQtycal
		from @Credit6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 0 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM2credit = sumQtycal
		from @Credit6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 1 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM3credit = sumQtycal
		from @Credit6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 2 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM4credit = sumQtycal
		from @Credit6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 3 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM5credit = sumQtycal
		from @Credit6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 4 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set LastSoldM6credit = sumQtycal
		from @Credit6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.branch = dst.Br
		where dst.code <>''  and c.Monthdiff = 5 and c.sumQtycal  > 0 and pr in (select pr from @UItemB)
--=====End of Chandra_F_Credit6month ===================================================================
--=====Proceddure Chandra_F_Trans6month==============================================================================
		declare @Trans
			table (DT_DOCCODE  nvarchar(4),Monthdiff int,Daydiff int,src nvarchar(2),Dst nvarchar(2),Docno nvarchar(24), DI_ACTIVE smallint,itemcode nvarchar(15),itemname nvarchar(150),
			skm_qty int/*,amt int*/)
		declare @Trans6m
			table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )
		declare @Trans6m02
			table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )
		declare @Trans6m03
			table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )
		declare @Trans6m04
			table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )

		insert into @Trans
		select DT_DOCCODE ,DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) AS Monthdiff,DATEDIFF(day, dbo.DOCINFO.DI_DATE, GETDATE()) AS Daydiff,
		case
			when SUBSTRING(dbo.DOCINFO.DI_REF, 2, 1) in ('1','2','3','4','5','6','7') then SUBSTRING(dbo.DOCINFO.DI_REF, 2, 1)
			else case when SUBSTRING(dbo.DOCINFO.DI_REF, 3, 1) in ('B') then '1' else SUBSTRING(dbo.DOCINFO.DI_REF, 3, 1) end end As SRC,
		case
			when SUBSTRING(dbo.DOCINFO.DI_REF, 4, 1) in ('1','2','3','4','5','6','7') then SUBSTRING(dbo.DOCINFO.DI_REF, 4, 1)
			else case  when SUBSTRING(dbo.DOCINFO.DI_REF, 4, 1) = 'O'then '3' end end As Dst ,
				dbo.DOCINFO.DI_REF AS Docno,
				dbo.DOCINFO.DI_ACTIVE,
				SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
				dbo.SKUMASTER.SKU_NAME AS itemname,
				skm_qty
		from doctype join docinfo on dt_key=di_dt join
				transtkh on di_key=trh_di join
				skumove  on di_key=skm_di join
				SKUMASTER on skm_sku=sku_key
		where DT_PROPERTIES in ('311') and DT_DOCCODE not in ('T1A','TA1','T2A','TA2','TA1A','TA2A') and
				DI_ACTIVE='0' and
				skm_qty >0 /*and DT_DOCCODE = 'TAB2' */and
				(DATEDIFF(month, dbo.DOCINFO.DI_DATE, GETDATE()) < 6)  and
				SKU_KEY in (select sku_key  from docinfo join  transtkh on di_key=trh_di join
										transtkd on trh_key=TRD_TRH join skumaster on trd_sku=sku_key  /*where dbo.SKUMASTER.SKU_CODE= '0206090009'*/)

		--==========================================
		insert into @Trans6m
		select a.itemcode,a.Monthdiff,
					sum(CASE
				  WHEN a.src = '1' THEN skm_Qty * -1
				  WHEN a.dst = '1' THEN skm_Qty
				  WHEN a.src = '7' THEN skm_Qty * -1
				  WHEN a.dst = '7' THEN skm_Qty end) AS Sumqtycal, '1'
		from @trans as a
		where  (a.src = '1' or a.dst = '1'or a.src = '7' or a.dst = '7')
		group by a.itemcode,a.Monthdiff
			order by itemcode,Monthdiff  asc

		--===========================

		UPDATE Chandra_Report set TransM1 = sumQtycal
		from @Trans6m  as c inner join Chandra_Report as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM2 = sumQtycal
		from @Trans6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where  c.Monthdiff = 1  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM3 = sumQtycal
		from @Trans6m as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 2  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM4 = sumQtycal
		from @Trans6m as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 3 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM5 = sumQtycal
		from @Trans6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 4  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM6 = sumQtycal
		from @Trans6m  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 5  and pr in (select pr from @UItemB)


		--=====สาขา 2=====================================
		insert into @Trans6m02
		select a.itemcode,a.Monthdiff,
					sum(CASE
						  WHEN a.src = '2' THEN skm_Qty * -1
						  WHEN a.dst = '2' THEN skm_Qty end) AS Sumqtycal, '2'
		from @trans as a
		where (a.src = '2' or a.dst = '2')
		group by a.itemcode,a.Monthdiff
			order by itemcode,Monthdiff  asc

		--===========================

		UPDATE Chandra_Report set TransM1 = sumQtycal
		from @Trans6m02  as c inner join Chandra_Report as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM2 = sumQtycal
		from @Trans6m02  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where  c.Monthdiff = 1  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM3 = sumQtycal
		from @Trans6m02 as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 2  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM4 = sumQtycal
		from @Trans6m02 as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 3 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM5 = sumQtycal
		from @Trans6m02  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 4  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM6 = sumQtycal
		from @Trans6m02  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 5  and pr in (select pr from @UItemB)

		--======สาขา 3 ====================================
		insert into @Trans6m03
		select a.itemcode,a.Monthdiff,
					sum(CASE
						  WHEN a.src = '3' THEN skm_Qty * -1
						  WHEN a.dst = '3' THEN skm_Qty end) AS Sumqtycal, '3'
		from @trans as a
		where (a.src = '3' or a.dst = '3')
		group by a.itemcode,a.Monthdiff
			order by itemcode,Monthdiff  asc

		--===========================

		UPDATE Chandra_Report set TransM1 = sumQtycal
		from @Trans6m03  as c inner join Chandra_Report as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM2 = sumQtycal
		from @Trans6m03  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where  c.Monthdiff = 1  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM3 = sumQtycal
		from @Trans6m03 as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 2  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM4 = sumQtycal
		from @Trans6m03 as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 3 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM5 = sumQtycal
		from @Trans6m03  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 4  and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM6 = sumQtycal
		from @Trans6m03  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 5  and pr in (select pr from @UItemB)

		--=============สาขา 4=============================
		insert into @Trans6m04
		select a.itemcode,a.Monthdiff,
					sum(CASE
						  WHEN a.src = '4' THEN skm_Qty * -1
						  WHEN a.dst = '4' THEN skm_Qty end) AS Sumqtycal, '4'
		from @trans as a
		where (a.src = '4' or a.dst = '4')
		group by a.itemcode,a.Monthdiff
			order by itemcode,Monthdiff  asc

		--===========================

		UPDATE Chandra_Report set TransM1 = sumQtycal
		from @Trans6m04  as c inner join Chandra_Report as dst on c.itemcode = dst.code and c.br = dst.Br
		where c.Monthdiff = 0 and pr in (select pr from @UItemB)

		UPDATE Chandra_Report set TransM2 = sumQtycal
		from @Trans6m04  as c inner join Chandra_Report  as dst on c.itemcode = dst.code and c.br = dst.Br
		where  c.Monthdiff = 1  and pr in (select pr from @UItemB)

