			select ROW_NUMBER ()OVER( PARTITION BY dbo.SKUMASTER.SKU_CODE order by docinfo.di_date desc) as N,
					DT_DOCCODE ,dbo.DOCINFO.DI_DATE as Docdate,DATEDIFF(day, dbo.DOCINFO.DI_DATE, GETDATE()) AS Daydiff,
			case
				when SUBSTRING(dbo.DOCINFO.DI_REF, 2, 1) in ('1','2','3','4','5','6','7') then SUBSTRING(dbo.DOCINFO.DI_REF, 2, 1)
				else case when SUBSTRING(dbo.DOCINFO.DI_REF, 3, 1) in ('B') then '1' else SUBSTRING(dbo.DOCINFO.DI_REF, 3, 1) end end As VenDorName,
			case
				when SUBSTRING(dbo.DOCINFO.DI_REF, 4, 1) in ('1','2','3','4','5','6','7') then SUBSTRING(dbo.DOCINFO.DI_REF, 4, 1)
