		on  ods_key=ARPLU_ ODS inner join UOFQTY on  ODS_UTQ=UTQ_KEY
		--where SUBSTRING(sku_CODE, 1, 10) like @pr
		where SUBSTRING(sku_CODE, 1, 10) in (select pr from @UItemB)

	insert @price
		select * from @p where n =1
			--==================================================

			UPDATE Chandra_Report
			SET Chandra_Report.price = src.price,
				Chandra_Report.Pack = src.pack
			from chandra_report as dst
			inner join  @price as src on src.itemcode=dst.pr
--=================================================================

--=========Chandra_F_Update_sumsold_Per @code======================
		declare @PrCode
				table( pr nvarchar(16),code nvarchar(16),Br nvarchar(2))

		insert  @PrCode
			select pr,code,BR
			from Chandra_Report
			where Chandra_Report.pr in (select pr from @UItemB)
			and code not in ('ยกเลิกรายการขายแล้ว','สินค้าป็นของแผนก 5','ไม่มีข้อมูล','1','ไม่รู้เป็นรายการไหน','ไม่เคยสั่งเข้า','')
--=====Update 1,2 ============================================
		update Chandra_Report
		set
			EstStock = case
							when isnull(t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0),0) = 0 then 0 else (t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)) end,
			per = case when t.avgsold = 0 then 0 else
						case when EstStock = 0 then 0 else ((t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0))/t.avgsold)*100  end end

		from Chandra_Report as t join @PRcode as tt on t.pr = tt.pr and t.code = tt.code and t.br = tt.Br
		where t.br in ( '1','2')

--=====Update 3,4 ============================================
		update Chandra_Report
		set
			EstStock = case
						when isnull(t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0),0) = 0 then 0 else (t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)) end,
			per = case when t.avgsold = 0 then 0 else
						case when EstStock = 0 then 0 else ((t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0))/t.avgsold)*100  end end

		from Chandra_Report as t join @PRcode as tt on t.pr = tt.pr and t.code = tt.code and t.br = tt.Br
		where t.br in ( '3','4')

--=====Update PerNew ============================================
		declare @PrC
			table( pr nvarchar(16),EstStockNew money,PerNew money,AvgSoldNew money)

		insert  @PrC
				select pr,
						 case
							when isnull(t.lastBuy1QtyNew-isnull(t.sumsoldNew,0),0) = 0 then 0 else t.lastBuy1QtyNew-isnull(t.sumsoldnew,0) end as EstStockNew,

						case when isnull(sum(t.sumlastsold),0) = 0 then 0
							else  case when isnull(t.lastBuy1QtyNew-isnull(t.sumsoldNew,0),0) = 0 then 0
							else  (t.lastBuy1QtyNew-isnull(t.sumsoldnew,0))*100 / (sum(sumlastsold)/6) end end as perNew,

						case when isnull(sum(t.sumlastsold),0) = 0 then 0 else cast (sum(t.sumlastsold) as decimal)/6  end as AvgSoldNew

				from chandra_report as t
				where t.pr in (select pr from @UItemB)
				group by t.pr,t.lastBuy1QtyNew,t.sumsoldNew

			update Chandra_Report
			set
				Chandra_Report.EstStockNew = tt.EstStockNew,
				Chandra_Report.AvgSoldNew = tt.AvgSoldNew,
				Chandra_Report.PerNew = tt.PerNew
			from Chandra_Report as t join @PrC as tt on t.pr = tt.pr


--=====Update MovingNew ============================================

		declare @PrMov
			table( pr nvarchar(16),br nvarchar(2),AvgSoldNew money,lastBuy1QtyNew money,MovingNew money,AvgSold money ,lastBuy1Qty int,Moving money)
		insert  @PrMov
				select pr,t.br,t.AvgSoldNew,t.lastBuy1QtyNew ,

				case when isnull(t.AvgSoldNew,0) = 0 then 0
				     when isnull(t.lastBuy1QtyNew,0) = 0 then 0
				     else  cast ((t.AvgSoldNew*100)/t.lastBuy1QtyNew as decimal) end as MovingNew,
				     t.AvgSold,t.lastBuy1Qty,
				case when isnull(t.AvgSold,0) = 0 then 0
				     when isnull(t.lastBuyTrans1Qty ,0) = 0 then 0
				     else  cast ((t.AvgSold*100)/t.lastBuyTrans1Qty as decimal) end as Moving
				from chandra_report as t
				where t.pr in (select pr from @UItemB)

			update Chandra_Report
			set
				Chandra_Report.MovingNew = tt.MovingNew,
				Chandra_Report.Moving = tt.Moving

			from Chandra_Report as t join @PrMov as tt on t.pr = tt.pr
			where t.Br = tt.br

--=============================================================================
return
End

