									  sumbuy float)
				insert @temp3
						select sku_code,sku_name,SKU_LAST_UCCOST,SKU_STD_COST,wl_code,
						       sum(case when dt_properties in (302,337,307,308) then SKM_QTY else 0 end) as sell,
							   sum(case when dt_properties in (311) then SKM_QTY else 0 end) as trans,
							   sum(case when dt_properties in (303,304) then SKM_QTY else 0 end) as buy
								from skumaster inner join skumove
								on sku_key=skm_sku inner join docinfo
								on skm_di=di_key inner join doctype
								on di_dt=dt_key inner join WARELOCATION
								on skm_wl=wl_key
								where (SKU_CODE>=@doc1 and SKU_code<=@doc2)
								and (di_date>=@date1 and di_date<=@date2)
								group by sku_code,sku_name,SKU_LAST_UCCOST,SKU_STD_COST,wl_code


				---------------------------------------------------------------------------------------------------------------------

				if(@choice=1)
				begin
						if(@doc1 ='' or @doc2='')
						begin
							select * from @Temp2 order by code
						end
						else if(@doc1 < @doc2)
						begin
							select * from @Temp2 where (code>=@doc2 and code<=@doc1) or (ic_code>=@doc2 and ic_code<=@doc1) order by code
						end
						else
						begin
							select * from @Temp2 where (code>=@doc1 and code<=@doc2) or (ic_code>=@doc1 and ic_code<=@doc2) order by code
						end
				end
				--ยอดรวมคงเหลือทั้งหมด
				if(@choice=2)
				begin
						if(@doc1 ='' or @doc2='')
						begin
							select * from @Temp2 where qty<0 order by code
						end
						else if(@doc1 < @doc2)
						begin
							select * from @Temp2 where ((code>=@doc2 and code<=@doc1) or (ic_code>=@doc2 and ic_code<=@doc1)) and qty<0 order by code
						end
						else
						begin
							select * from @Temp2 where ((code>=@doc1 and code<=@doc2) or (ic_code>=@doc1 and ic_code<=@doc2)) and qty<0 order by code
						end
				end
				--ยอดรวมคงเหลือทั้งหมดติดลบ

				if(@choice=3)
				begin
					if(@doc1 ='' or @doc2='')
					begin
						select * from @Temp order by code,wlcode
					end
					else if(@doc1 < @doc2)
					begin
						select * from @Temp where (code>=@doc2 and code<=@doc1) or (ic_code>=@doc2 and ic_code<=@doc1)
						order by code,wlcode
					end
					else
					begin
						select * from @Temp where (code>=@doc1 and code<=@doc2) or (ic_code>=@doc1 and ic_code<=@doc2)
						order by code,wlcode
					end

				end
				--ยอดคงเหลือแยกคลัง
				if(@choice=4)
				begin
					if(@doc1 ='' or @doc2='')
					begin
						select *,500 from @Temp where qty<=0 order by code,wlcode
					end
					else if(@doc1 < @doc2)
					begin
						select *,0 from @Temp where ((code>=@doc2 and code<=@doc1) or (ic_code>=@doc2 and ic_code<=@doc1)) and qty<0
						order by code,wlcode
					end
					else
					begin
						select *,0 from @Temp where ((code>=@doc1 and code<=@doc2) or (ic_code>=@doc1 and ic_code<=@doc2)) and qty<=0
						order by code,wlcode
					end
				--ยอดคงเหลือแยกคลังติดลบ
			end
				if(@choice=5)
				begin
					select * from @temp3
				end

END

/****** Object:  StoredProcedure [dbo].[CheckTA]    Script Date: 09/08/68 13:34:27 ******/
SET ANSI_NULLS ON

SET QUOTED_IDENTIFIER ON

-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [dbo].[CheckTA]
	@Choice int,
	@dateB date,
	@dateE date,
	@sku_code varchar(24)
AS
BEGIN
		declare @move
				table
					  (M_code varchar(24),
					  M_name varchar(60),
					  M_Qty money,
					  M_WL varchar(16),
					  M_WLname varchar(60)
					  )

		declare @item
				table(itemcode varchar(24),
					  itemname varchar(77),
					  QTY money,
					  Lcost float,
					  Scost float,
					  price float,
					  Disc	varchar(11),
					  price_disc float,
					  prof varchar(11)
					  )
		declare @temp
				table(N int,
					  docdate datetime,
					  docno varchar(16),
					  code varchar(60),
					  price float)
		declare @Buy1
				table(N1 int,
					  date1 datetime,
					  ref1 varchar(16),
					  code1 varchar(60),
					  price1 float)
		declare @Buy2
				table(N2 int,
					  date2 datetime,
					  ref2 varchar(16),
					  code2 varchar(60),
					  price2 float)
		declare @Buy3
				table(N3 int,
					  date3 datetime,
					  ref3 varchar(16),
					  code3 varchar(60),
					  price3 float)
				insert @temp
						select
								ROW_NUMBER ()OVER( PARTITION BY transtkd.trd_sh_code order by docinfo.di_date desc),
								docinfo.di_date ,
								docinfo.di_ref ,
								transtkd.trd_sh_code,
								transtkd.TRD_B_UPRC
						from
								doctype inner join
								docinfo on dt_key=docinfo.di_dt inner join
								transtkh on docinfo.di_key = trh_di inner join
								transtkd on trh_key = transtkd.trd_trh inner join
								skumaster on transtkd.TRD_SKU = sku_key
								where (dt_properties = '303'or dt_properties ='304')
								--or DT_PROPERTIES='302'or DT_PROPERTIES='337' or DT_PROPERTIES='308'or DT_PROPERTIES='307' )
				insert @Buy1
						select * from @temp where N=1
				insert @Buy2
						select * from @temp where N=2
				insert @Buy3
						select * from @temp where N=3


	if(@Choice=1)--ค้นหาเอกสารโอนย้ายที่ราคาไม่ตรงกับทุน
		begin
			begin
				if(@dateB='' or @dateE='')
					begin
						set @dateB=getdate()-1;
						set @dateE=GETDATE()-1;
					end

						select  transtkd.trd_sh_code as รหัสสินค้า,
								transtkd.TRD_SH_NAME as ชื่อสินค้า,
								transtkd.TRD_K_U_PRC as ราคาใบบิลT,
								(TRD_UTQQTY*SKU_std_COST) as ทุนฝ่ายขาย,
								(TRD_UTQQTY*SKU_LAST_UCCOST) as ทุนปัจจุบัน,
								price1 as ราคาสั่งซื้อล่าสุด,
								price2 as ราคาสั่งซื้อ2,
								price3 as ราคาสั่งซื้อ3,
								transtkd.TRD_G_KEYIN as ราคารวมในบิล,
								docinfo.di_ref as เลขที่เอกสารT,
								ref1 as เลขเอกสารซื้อล่าสุด,
								ref2 as เลขเอกสารซื้อ2,
								ref3 as เลขเอกสารซื้อ3,
								docinfo.di_date as วันที่เอกสารT,
								date1 as วันที่สั่งซื้อล่าสุด,
								date2 as วันที่สั่งซื้อ2,
								date3 as วันที่สั่งซื้อ3,
								transtkd.trd_qty as จำนวนใบบิล,
								transtkd.trd_utqname as หน่วย ,
								transtkd.TRD_UTQQTY as จำนวนต่อหน่วย,
								docinfo.DI_APV_STATUS as สถานะอัพเดท
								from	doctype inner join
										docinfo on doctype.dt_key=docinfo.di_dt inner join
										transtkh on docinfo.di_key = transtkh.trh_di inner join
										transtkd on trh_key = trd_trh inner join
										skumaster on transtkd.TRD_SKU = sku_key full outer join
										@Buy1 on sku_code=code1 full outer join
										@Buy2 on sku_code=code2 full outer join
										@Buy3 on sku_code=code3
												where dt_properties = '311' and
													  DI_ACTIVE ='0'and
													  docinfo.di_date >= @dateB and
													  docinfo.di_date <= @dateE and
													  ((transtkd.TRD_K_U_PRC != (TRD_UTQQTY*SKU_LAST_UCCOST)) or SKU_STD_COST='0')
													  and transtkd.TRD_K_U_PRC<> (TRD_UTQQTY*SKU_std_COST)
													  and docinfo.di_ref not like 'TA1%' and docinfo.di_ref not like 'TA2%'
													  and docinfo.di_ref not like 'T1A%' and docinfo.di_ref not like 'T2A%'
													  and docinfo.di_ref not like 'T1I%' and docinfo.di_ref not like 'T2I%'
														order by di_date,di_ref


						select  transtkd.trd_sh_code as รหัสสินค้า,
								transtkd.TRD_SH_NAME as ชื่อสินค้า,
								transtkd.TRD_K_U_PRC as ราคาใบบิลT,
								(TRD_UTQQTY*SKU_std_COST) as ทุนฝ่ายขาย,
								(TRD_UTQQTY*SKU_LAST_UCCOST) as ทุนปัจจุบัน,
								price1 as ราคาสั่งซื้อล่าสุด,
								price2 as ราคาสั่งซื้อ2,
								price3 as ราคาสั่งซื้อ3,
								transtkd.TRD_G_KEYIN as ราคารวมในบิล,
								docinfo.di_ref as เลขที่เอกสารT,
								ref1 as เลขเอกสารซื้อล่าสุด,
								ref2 as เลขเอกสารซื้อ2,
								ref3 as เลขเอกสารซื้อ3,
								docinfo.di_date as วันที่เอกสารT,
								date1 as วันที่สั่งซื้อล่าสุด,
								date2 as วันที่สั่งซื้อ2,
								date3 as วันที่สั่งซื้อ3,
								transtkd.trd_qty as จำนวนใบบิล,
								transtkd.trd_utqname as หน่วย ,
								transtkd.TRD_UTQQTY as จำนวนต่อหน่วย,
								docinfo.DI_APV_STATUS as สถานะอัพเดท
								from	doctype inner join
										docinfo on doctype.dt_key=docinfo.di_dt inner join
										transtkh on docinfo.di_key = transtkh.trh_di inner join
										transtkd on trh_key = trd_trh inner join
										skumaster on transtkd.TRD_SKU = sku_key full outer join
										@Buy1 on sku_code=code1 full outer join
										@Buy2 on sku_code=code2 full outer join
										@Buy3 on sku_code=code3
												where dt_properties = '311' and
													  DI_ACTIVE ='0'and
													  docinfo.di_date >= @dateB and
													  docinfo.di_date <= @dateE
													  and transtkd.TRD_K_U_PRC <> (TRD_UTQQTY*SKU_std_COST)
													  and docinfo.di_ref not like 'TA1%' and docinfo.di_ref not like 'TA2%'
													  and docinfo.di_ref not like 'T1A%' and docinfo.di_ref not like 'T2A%'
													  and docinfo.di_ref not like 'T1I%' and docinfo.di_ref not like 'T2I%'
														order by di_date,di_ref
					end
		end
	else if(@Choice=2)
		begin
				begin
				insert @item
				select	 ods_code as รหัสสินค้า,
							sku_name+'('+utq_name+')',
							UTQ_QTY ,
							SKU_LAST_UCCOST,
							sku_std_cost,
							ARPLU_U_PRC,
							convert(varchar(10),(case
								when ARPLU_U_DSC = '' then 0
								when ARPLU_U_DSC is null then 0
								else ARPLU_U_DSC
								end)) +'%',

							(ARPLU_U_PRC-((ARPLU_U_PRC*isnull(ARPLU_U_DSC,0))/100)),

							convert(varchar(10),(isnull(round(((((ARPLU_U_PRC-((ARPLU_U_PRC*ARPLU_U_DSC)/100))-
							Nullif (SKU_std_COST,0))/(ARPLU_U_PRC-
							((ARPLU_U_PRC*isnull(ARPLU_U_DSC,0))/100)))*100),2,1),0)))+'%'
							from  ODSMASTER inner join skumaster on
								  ODS_SKU = sku_key  inner join UOFQTY on
								  ODS_UTQ=UTQ_KEY inner join arplu on
								  ods_key=ARPLU_ ODS
				end
				begin
				insert @move
					select  SKUMASTER.sku_code,
							SKUMASTER.sku_name,
							sum(skumove.skm_QTY),
							WARELOCATION.WL_code,
							WARELOCATION.WL_name
						from SKUMASTER inner join SKUMOVE on sku_key=skm_sku inner join
						WARELOCATION on skm_wl=wl_key
							group by skumaster.sku_key
							,skumove.skm_sku,
							skumaster.sku_code,
							skumaster.sku_name,
							WARELOCATION.WL_code,
							WARELOCATION.WL_name
								order by sku_code,wl_name
				end
						if(@sku_code='')
							BEGIN
								select itemcode as รหัสสินค้า,
									   itemname as ชื่อสินค้า,
									   QTY as จำนวนต่อหน่วย,
									   M_Qty as คงเหลือ,
									   M_wlname as คลังเก็บ,
									   Lcost as ทุนปัจจุบัน,
									   Scost as ทุนฝ่ายขาย,
									   price as ราคาขาย,
									   Disc as ส่วนลดจากระบบ,
									   price_disc as ราคาหลังหักส่วนลดจากระบบ,
									   prof as กำไรเป็นเปอร์เซ็น
									   from @item inner join @move on itemcode=m_code order by itemcode
							end
						else
							begin
								select itemcode as รหัสสินค้า,
									   itemname as ชื่อสินค้า,
									   QTY as จำนวนต่อหน่วย,
									   M_Qty as คงเหลือ,
									   M_wlname as คลังเก็บ,
									   Lcost as ทุนปัจจุบัน,
									   Scost as ทุนฝ่ายขาย,
									   price as ราคาขาย,
									   Disc as ส่วนลดจากระบบ,
									   price_disc as ราคาหลังหักส่วนลดจากระบบ,
									   prof as กำไรเป็นเปอร์เซ็น
									   from @item inner join @move on itemcode=m_code
									   where (itemcode like '%'+@sku_code+'%' or itemname like '%'+@sku_code+'%') order by itemcode
							end
						end
	else if(@Choice=5)--ประวัติการขาย
		begin
			select  di_date,
				di_ref,
				trd_sh_code,
				trd_sh_name,
				TRD_SH_QTY,
				TRD_K_U_PRC,
				SKU_LAST_UCCOST
			from doctype inner join docinfo on dt_key = di_dt
				 inner join TRANSTKH on di_key=trh_di
				 inner join transtkd on trh_key=trd_trh
				 inner join  ODSMASTER on TRD_ ODS =  ODS_KEY
				 inner join SKUMASTER on  ODS_SKU = sku_key
				where di_date between @dateB  and @dateE
					and (dt_properties='303' or dt_properties='311' )
					and trd_k_u_prc!=sku_last_uccost
			order by di_date
		end
	else if(@Choice=6)--ประวัติการซื้อ
	begin
			select docdate,SKU_CODE,SKU_NAME,docno,price
			from @temp inner join skumaster on code=sku_code
				where code like '%'+@sku_code+'%' or code like '%'+@sku_code+'%'
			order by N
	end
	else if(@Choice=10)--อัพเดทราคาทุนลงบนใบโอนย้าย
		begin
						begin
								update TRANSTKD  set
										trd_k_u_prc = SKU_LAST_UCCOST*TRD_UTQQTY,
										trd_u_prc = SKU_LAST_UCCOST*TRD_UTQQTY,
										trd_g_keyin = ((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_g_sell = ((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_g_vat='0',
										trd_g_amt= ((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_n_sell = ((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_n_vat='0',
										trd_n_amt=((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_b_sell=((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_b_vat='0',
										trd_b_amt=SKU_LAST_UCCOST*TRD_UTQQTY,
										trd_b_relocate=SKU_LAST_UCCOST*TRD_UTQQTY,
										trd_sh_uprc=SKU_LAST_UCCOST*TRD_UTQQTY,
										trd_sh_gsell=((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST),
										trd_sh_gvat='0',
										trd_sh_gamt=((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST)
										from	doctype inner join
												docinfo on dt_key=di_dt inner join
												transtkh on di_key = trh_di inner join
												transtkd on trh_key = trd_trh inner join
												skumaster on TRD_SKU = sku_key
												where	DT_PROPERTIES='311' and
														DI_ACTIVE ='0' and
														DI_APV_STATUS = '0' and
														di_date >= @dateB and
														di_date <= @dateE
						end
						begin
								select di_ref as docno,di_amount as amount ,sum(((TRD_UTQQTY*trd_QTY)*SKU_LAST_UCCOST))
											over(partition by di_ref) as COST into CostamounT
										from	doctype inner join
												docinfo on dt_key=di_dt inner join
												transtkh on di_key = trh_di inner join
												transtkd on trh_key = trd_trh inner join
												skumaster on TRD_SKU = sku_key
												where   DT_PROPERTIES='311' and
														DI_ACTIVE ='0' and
														DI_APV_STATUS = '0' and
														di_date >= @dateB and
														di_date <= @dateE
						end
						begin
								update DOCINFO set di_amount=cost ,
												   DI_APV_STATUS='3',
												   DI_APV_DATE=getdate(),
												   DI_APV_BY='DESKTOP-HVLNPJN',
												   DI_APV_LGNN='TUM',
												   DI_APV_CPTN='Update_By_Query'
								from docinfo inner join CostamounT on di_ref = docno
						end
						begin
								drop table CostamounT
						end
		end
	else
		begin
			print 'กรุณาเลือกหัวข้อก่อนกดตกลง'
		end

END

