CREATE PROCEDURE [dbo].[Discount]
	@percent INT,
	@dateB	date,
	@dateE	date
AS
BEGIN
	select TRD_SEQ as ลำดับ,
	   di_ref as รหัสเอกสาร,
	   di_date as วันที่เอกสาร,
	   trd_keyin as รหัสสินค้า,
	   trd_sh_name as ชื่อสินค้า,
	   TRD_QTY as จำนวนต่อรายการ,
	   utq_name as หน่วย,
	   TRD_NM_PRC as ราคาขายตั้งต้น,
	   ARPLU_U_DSC as ส่วนลดตั้งต้น,
	   TRD_K_U_PRC as ราคาขายจากการคีย์,
	   trd_dsc_keyin as ส่วนลดจากการคีย์,
	   trd_g_sell as ราคารวมต่อรายการ,
	   sku_last_uccost as ทุนต่อตัว,
	   (SKU_LAST_UCCOST*UTQ_QTY)as ทุนรวม,
	   utq_qty as จำนวนต่อหน่วย,
	   ARPLU_U_PRC as ราคาขายระบบ,
	   TRD_K_U_PRC as ราคาหลังใส่ส่วนลด,
	   (((trd_k_u_prc-(SKU_LAST_UCCOST*UTQ_QTY))/(SKU_LAST_UCCOST*UTQ_QTY))*100) as กำไร,
	   (ARPLU_U_PRC-((ARPLU_U_PRC*@percent)/100)) as ลดตามจริง
from	 doctype inner join docinfo on
		 dt_key=di_dt inner join TRANSTKH on
		 di_key=trh_di inner join transtkd on
		 trh_key = trd_trh inner join  ODSMASTER on
		 TRD_ ODS =  ODS_KEY inner join skumaster on
		  ODS_SKU = sku_key  inner join UOFQTY on
		  ODS_UTQ=UTQ_KEY inner join arplu on
		  ods_key=ARPLU_ ODS
		 where trd_k_u_prc<=(ARPLU_U_PRC-((ARPLU_U_PRC*@percent)/100)) and
		 DT_PROPERTIES='302' and di_ref like 'c%'and di_date between @dateB and @dateE
		 order by di_date
END

