		@avgsold float,
		@sumlastbuy int

    -- Insert statements for procedure here

		declare @PrCode
				table( pr nvarchar(16),code nvarchar(16),Br nvarchar(2))

		insert  @PrCode
			select pr,code,BR
			from Percent_Stock_Report_New--left join @BuyLog on Itemcode  = Percent_Stock_Report_New.pr
			where Percent_Stock_Report_New.pr like @Doc
			and code not in ('ยกเลิกรายการขายแล้ว','สินค้าป็นของแผนก 5','ไม่มีข้อมูล','1','ไม่รู้เป็นรายการไหน','ไม่เคยสั่งเข้า','')
--=====Update 1,2 ============================================
update Percent_Stock_Report_new
set
	EstStock = case
					when isnull(t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0),0) = 0 then 0 else (t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)) end,
	/*	when isnull(t.lastBuyTrans1Qty,0)=0 then t.lastBuy1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)
		when t.lastBuyTrans1Qty>0 then  t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0) end,*/
	per = case when t.avgsold = 0 then 0 else
				case when t.EstStock = 0 then 0 else (t.EstStock/t.avgsold)*100 end end

from Percent_Stock_Report_New as t join @PRcode as tt on t.pr = tt.pr and t.code = tt.code and t.br = tt.Br
where t.br in ( '1','2')

--=====Update 3,4 ============================================
	update Percent_Stock_Report_New
	set
		EstStock = case
					when isnull(t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0),0) = 0 then 0 else (t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)) end,
					--when t.lastBuyTrans1Qty>0 then  t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0) end,
		per = case when t.avgsold = 0 then 0 else
					case when t.EstStock = 0 then 0 else (t.EstStock/t.avgsold)*100 end end

	from Percent_Stock_Report_New as t join @PRcode as tt on t.pr = tt.pr and t.code = tt.code and t.br = tt.Br
	where t.br in ( '3','4')
--========================================================================================
declare @CashFull table (D date, BranCh nvarchar(2),DI_ACTIVE smallint,itemcode nvarchar(16),amt int)

	insert into @CashFull
		SELECT dbo.docinfo_temp.DI_DATE as d,
			case when SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) = '7' then '1'
		     when SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) <> '7' then SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) end AS BranCh
		,dbo.docinfo_temp.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
						  CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '337' THEN (skumove.skm_Qty) * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '302' THEN (skumove.skm_Qty)
						  * - 1 END AS amt
		FROM     dbo.DOCTYPE INNER JOIN
						  dbo.docinfo_temp ON dbo.DOCTYPE.DT_KEY = dbo.docinfo_temp.DI_DT INNER JOIN
						  dbo.TRANSTKH ON dbo.docinfo_temp.DI_KEY = dbo.TRANSTKH.TRH_DI INNER JOIN
