	, dbo.docinfo_temp.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
					  CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '307' THEN (skumove.skm_Qty) * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '308' THEN (skumove.skm_Qty)
					  * - 1 END AS amt
