					  dbo.SKUMASTER ON dbo.SKUMOVE.SKM_SKU = dbo.SKUMASTER.SKU_KEY
	WHERE  /*DATEDIFF(DAY, dbo.DOCINFO.DI_DATE, GETDATE()) < 700 and */(dbo.docinfo_temp.DI_ACTIVE = '0') AND SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) is not null AND (dbo.DOCTYPE.DT_PROPERTIES IN ('307', '308')) AND (dbo.SKUMASTER.SKU_KEY IN
						  (SELECT SKUMASTER_1.SKU_KEY
