											 dbo.SKUMASTER AS SKUMASTER_1 ON dbo.TRANSTKD.TRD_SKU = SKUMASTER_1.SKU_KEY))


--=======Chandra_F_Update_SumSoldCashNew==================================================
	declare @Cash02 table (itemcode nvarchar(16),lastBuy1datenew date/*, Br nvarchar(2)*/,Sumqty int)

