--
AS
BEGIN
SET NOCOUNT ON;
SET ANSI_WARNINGS OFF
		--	declare @pr nvarchar(15)

			--set @pr = 'p2106411/00057'

	 	declare @UItem table (code nvarchar(15),name nvarchar(150),Pr nvarchar(15),pack nvarchar(15),Br nvarchar(2))
		insert @UItem
			select code,Name,pr,Pack,Br
			from Percent_Stock_Report_New
			where pr =  /*'P1086401/00002'*/@Pr and code <>'' and code <>'-'
			group by code,Name,pr,Pack,Br




			declare @p table (N int,itemname nvarchar(150),itemcode nvarchar(15),price money,pack nvarchar(15))

			declare @price table (N int,itemname nvarchar(150),itemcode nvarchar(15),price money,pack nvarchar(15))

			insert @p
				select ROW_NUMBER() OVER(PARTITION BY  SKU_NAME  ORDER BY ARPLU_U_PRC asc) as N,SKU_NAME as itemname,SUBSTRING(sku_CODE, 1, 10) as itemcode,
				ARPLU_U_PRC as Price ,UTQ_NAME
				from skumaster inner join  ODSMASTER
				on sku_key= ods_sku inner join arplu
				on  ods_key=ARPLU_ ODS inner join UOFQTY on  ODS_UTQ=UTQ_KEY
				--where SUBSTRING(sku_CODE, 1, 10) = '0504010005'
				where SKU_KEY in
				(select sku_key
				/*from docinfo_temp join transtkh */
				from Percent_PR_REF join transtkh
				on di_key=trh_di join transtkd
				on trh_key=TRD_TRH join skumaster
				on trd_sku=sku_key  where di_ref =  @pr)

			insert @price
				select * from @p where n =1

	--==================================================

							UPDATE Percent_Stock_Report_New
							SET Percent_Stock_Report_New.price = src.price,
								Percent_Stock_Report_New.Pack = src.pack

							from Percent_Stock_Report_New as dst
							inner join  @price as src on src.itemname=dst.name
							where dst.code <>'' and dst.pr = @pr
--=======Chandra_F_Sum6month==================================================

declare @Sum6M table (itemcode nvarchar(15),BranCh nvarchar(2),Sumlastsale int,Sumsold int)

insert into @Sum6M
select code,br,
	isnull(LastSoldM1Cash,0) + isnull(LastSoldM2Cash,0) +isnull(LastSoldM3Cash,0) +isnull(LastSoldM4Cash,0) +isnull(LastSoldM5Cash,0) +
	isnull(LastSoldM6Cash,0) +isnull(LastSoldM1Credit,0) + isnull(LastSoldM2Credit,0) + isnull(LastSoldM3Credit,0) +
	isnull(LastSoldM4Credit,0) +isnull(LastSoldM5Credit,0) +isnull(LastSoldM6Credit,0)/* +
	isnull(TransM1,0) +isnull(TransM2,0) +isnull(TransM3,0) +isnull(TransM4,0) +isnull(TransM5,0) +isnull(TransM6,0)*/ as Sumlastsale,
	isnull(sumsoldcash,0) +isnull(sumsoldcredit,0)/*+isnull(sumsoldTrans,0)*/  as Sumsold

from Percent_Stock_Report_New
where  code  in (select code  from @Uitem)
and code not in ('ยกเลิกรายการขายแล้ว','สินค้าป็นของแผนก 5','ไม่มีข้อมูล','1','ไม่รู้เป็นรายการไหน','ไม่เคยสั่งเข้า','')

	UPDATE Percent_Stock_Report_New
	set sumlastsold = t2.Sumlastsale,
		sumsold = t2.Sumsold,
		avgSold = case when t2.Sumlastsale>0 then cast (t2.Sumlastsale as decimal)/6 else 0  end

	from Percent_Stock_Report_New as t1 inner join @Sum6M as t2 on t1.code = t2.itemcode and t1.br = t2.Branch

--=======End OF Chandra_F_Sum6month==================================================
	--=========Chandra_F_Update_sumsold_Per @code======================
		declare @PrCode
				table( pr nvarchar(16),code nvarchar(16),Br nvarchar(2))

		insert  @PrCode
			select pr,code,BR
			from Percent_Stock_Report_New --left join @BuyLog on Itemcode  = Percent_Stock_Report_New.pr
			where Percent_Stock_Report_New.pr = @Pr -- in ('0705070026'/*select code from @Uitem*/)
			and code not in ('ยกเลิกรายการขายแล้ว','สินค้าป็นของแผนก 5','ไม่มีข้อมูล','1','ไม่รู้เป็นรายการไหน','ไม่เคยสั่งเข้า','')
--=====Update 1,2 ============================================
		update Percent_Stock_Report_New
		set
			EstStock = case
						when isnull(t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0),0) = 0 then 0 else (t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)) end,
			per = case when t.avgsold = 0 then 0 else
						--case when EstStock = 0 then 0 else (-2/t.avgsold)*100 end end
						case when EstStock = 0 then 0 else ((t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0))/t.avgsold)*100 end end
		from Percent_Stock_Report_New as t inner join @PRcode as tt on t.pr = tt.pr and t.code = tt.code  and t.br = tt.Br
		where t.br in ( '1','2')

--=====Update 3,4 ============================================
		update Percent_Stock_Report_New
		set
			EstStock = case
						when isnull(t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0),0) = 0 then 0 else (t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0)) end,
			per = case when t.avgsold = 0 then 0 else
						case when EstStock = 0 then 0 else ((t.lastBuyTrans1Qty-isnull(t.sumsold,0)+isnull(t.sumsoldTrans,0))/t.avgsold)*100 end end

		from Percent_Stock_Report_New as t inner join @PRcode as tt on t.pr = tt.pr and t.code = tt.code and t.br = tt.Br
		where t.br in ( '3','4')

--=====Update PerNew ============================================
		declare @PrC
			table( pr nvarchar(16),code nvarchar(16),EstStockNew money,PerNew money,AvgSoldNew money/*,sumsoldcashnew int ,sumsoldcreditnew int*/,sumsoldnew int)


		insert  @PrC
				select pr,code,

						isnull(t.lastBuy1QtyNew,0)- t.sumsoldcreditnew - t.sumsoldcashnew  as EstStockNew,

						case when isnull(sum(t.sumlastsold),0) = 0 then 0
							else
								case when (isnull(t.lastBuy1QtyNew,0)- t.sumsoldcashnew - t.sumsoldcreditnew )= 0 then 0
							     else  (t.lastBuy1QtyNew - t.sumsoldcashnew - t.sumsoldcreditnew)*100 / (sum(t.sumlastsold)/6) end end as perNew,

						case when isnull(sum(t.sumlastsold),0) = 0 then 0 else cast (sum(t.sumlastsold) as decimal)/6  end as AvgSoldNew,
						 t.sumsoldcreditnew + t.sumsoldcashnew  as sumsoldnew

				from Percent_Stock_Report_New as t
				where t.pr like @Pr
				group by t.pr,t.code,t.lastBuy1QtyNew,t.sumsoldcashnew,t.sumsoldcreditnew,sumsoldnew

			update Percent_Stock_Report_new
			set
				Percent_Stock_Report_New.EstStockNew = tt.EstStockNew,
				Percent_Stock_Report_New.AvgSoldNew = tt.AvgSoldNew,
				Percent_Stock_Report_New.PerNew = tt.PerNew,
				Percent_Stock_Report_New.sumsoldnew = tt.sumsoldnew

			from Percent_Stock_Report_New as t join @PrC as tt on t.pr = tt.pr and t.code = tt.code


END

