declare @Cash6m table (Monthdiff int,BranCh nvarchar(2),itemcode nvarchar(10),Sumqtycal int)

insert into @Cash
SELECT DATEDIFF(month, dbo.docinfo_temp.DI_DATE, GETDATE()) AS Monthdiff, SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) AS BranCh, dbo.docinfo_temp.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
                  dbo.SKUMASTER.SKU_NAME AS itemname, dbo.docinfo_temp.DI_REF AS Docno, CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '337' THEN (skumove.skm_Qty) * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '302' THEN (skumove.skm_Qty)
                  * - 1 END AS amt
FROM     dbo.DOCTYPE INNER JOIN
                  dbo.docinfo_temp ON dbo.DOCTYPE.DT_KEY = dbo.docinfo_temp.DI_DT INNER JOIN
                  dbo.TRANSTKH ON dbo.docinfo_temp.DI_KEY = dbo.TRANSTKH.TRH_DI INNER JOIN
                  dbo.SKUMOVE ON dbo.docinfo_temp.DI_KEY = dbo.SKUMOVE.SKM_DI INNER JOIN
                  dbo.SKUMASTER ON dbo.SKUMOVE.SKM_SKU = dbo.SKUMASTER.SKU_KEY
WHERE   DATEDIFF(month, dbo.docinfo_temp.DI_DATE, GETDATE()) < 8 and (dbo.docinfo_temp.DI_ACTIVE = '0') And dbo.docinfo_temp.DI_REF  not like 'P%' AND (dbo.DOCTYPE.DT_PROPERTIES IN ('302', '337')) AND (dbo.SKUMASTER.SKU_KEY IN
                      (SELECT SKUMASTER_1.SKU_KEY
                       FROM      dbo.docinfo_temp AS DOCINFO_1 INNER JOIN
                                         dbo.TRANSTKH AS TRANSTKH_1 ON DOCINFO_1.DI_KEY = TRANSTKH_1.TRH_DI INNER JOIN
                                         dbo.TRANSTKD ON TRANSTKH_1.TRH_KEY = dbo.TRANSTKD.TRD_TRH INNER JOIN
                                         dbo.SKUMASTER AS SKUMASTER_1 ON dbo.TRANSTKD.TRD_SKU = SKUMASTER_1.SKU_KEY))
insert into @Cash6m
select c.Monthdiff,case when branch = 7 then 1 else branch end as branch,c.itemcode,SUM(c.Amt) as Sumqtycal
from @cash as c where c.Monthdiff <7
group by c.itemcode,c.Monthdiff,branch

UPDATE Percent_Stock_Report_New set LastSoldM1cash = sumQtycal
from @Cash6m  as c inner join Percent_Stock_Report_New  as dst on itemcode = dst.code
where dst.code <>'' and c.branch = dst.Br and c.Monthdiff = 0 and c.sumQtycal  > 0 and pr = @doc;

UPDATE Percent_Stock_Report_New set LastSoldM2cash = sumQtycal
from @Cash6m  as c inner join Percent_Stock_Report_New  as dst on itemcode = dst.code
where dst.code <>'' and c.branch = dst.Br and c.Monthdiff = 1 and c.sumQtycal  > 0 and pr = @doc;

UPDATE Percent_Stock_Report_New set LastSoldM3cash = sumQtycal
from @Cash6m  as c inner join Percent_Stock_Report_New  as dst on itemcode = dst.code
where dst.code <>'' and c.branch = dst.Br and c.Monthdiff = 2 and c.sumQtycal  > 0 and pr = @doc;

UPDATE Percent_Stock_Report_New set LastSoldM4cash = sumQtycal
from @Cash6m  as c inner join Percent_Stock_Report_New  as dst on itemcode = dst.code
where dst.code <>'' and c.branch = dst.Br and c.Monthdiff = 3 and c.sumQtycal  > 0 and pr = @doc;

UPDATE Percent_Stock_Report_New set LastSoldM5cash = sumQtycal
from @Cash6m  as c inner join Percent_Stock_Report_New  as dst on itemcode = dst.code
where dst.code <>'' and c.branch = dst.Br and c.Monthdiff = 4 and c.sumQtycal  > 0 and pr = @doc;

UPDATE Percent_Stock_Report_New set LastSoldM6cash = sumQtycal
from @Cash6m  as c inner join Percent_Stock_Report_New  as dst on itemcode = dst.code
where dst.code <>'' and c.branch = dst.Br and c.Monthdiff = 5 and c.sumQtycal  > 0 and pr = @doc;


return
END




