CREATE PROCEDURE [dbo].[percent_NS4_Trans6month]
	-- Add the parameters for the stored procedure here
	@doc nvarchar(15)
AS
BEGIN
SET NOCOUNT ON;

DECLARE
						@code nvarchar(36),
						@Depart  nvarchar(10),
						@sumQtycal  float,
						@m int,
						@LastSoldM1Cash float,
						@LastSoldM2Cash float,
						@LastSoldM3Cash float,
						@LastSoldM4Cash float,
						@LastSoldM5Cash float,
						@LastSoldM6Cash float,
						@name nvarchar(50),
						@Chk int,
						@sumlastsold float

------สาขา 2 update lastSoldM? -----------
declare @Trans
	table (DT_DOCCODE  nvarchar(4),Monthdiff int,Daydiff int,src nvarchar(2),Dst nvarchar(2),Docno nvarchar(24), DI_ACTIVE smallint,itemcode nvarchar(15),itemname nvarchar(150),
	skm_qty int/*,amt int*/)
declare @Trans6m
	table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )
declare @Trans6m02
	table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )
declare @Trans6m03
	table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )
declare @Trans6m04
	table (itemcode nvarchar(15),Monthdiff int,Sumqtycal int,Br nvarchar(2) )

insert into @Trans
select DT_DOCCODE ,DATEDIFF(month, dbo.docinfo_temp.DI_DATE, GETDATE()) AS Monthdiff,DATEDIFF(day, dbo.docinfo_temp.DI_DATE, GETDATE()) AS Daydiff,
case
	when SUBSTRING(dbo.docinfo_temp.DI_REF, 2, 1) in ('1','2','3','4','5','6','7') then SUBSTRING(dbo.docinfo_temp.DI_REF, 2, 1)
	else case when SUBSTRING(dbo.docinfo_temp.DI_REF, 3, 1) in ('B') then '1' else SUBSTRING(dbo.docinfo_temp.DI_REF, 3, 1) end end As SRC,
case
	when SUBSTRING(dbo.docinfo_temp.DI_REF, 4, 1) in ('1','2','3','4','5','6','7') then SUBSTRING(dbo.docinfo_temp.DI_REF, 4, 1)
	else case  when SUBSTRING(dbo.docinfo_temp.DI_REF, 4, 1) = 'O'then '3' end end As Dst ,
		dbo.docinfo_temp.DI_REF AS Docno,
		dbo.docinfo_temp.DI_ACTIVE,
		SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
		dbo.SKUMASTER.SKU_NAME AS itemname,
		skm_qty
from doctype join dbo.docinfo_temp on dt_key=di_dt join
		transtkh on di_key=trh_di join
		skumove  on di_key=skm_di join
		SKUMASTER on skm_sku=sku_key
