CREATE PROCEDURE [dbo].[Percent_NS7_Update_SumSoldCash]
		@Pr nvarchar(16)
AS
BEGIN
	SET NOCOUNT ON
	declare @Cash table (D date, BranCh nvarchar(2),DI_ACTIVE smallint,itemcode nvarchar(16),amt int)

	insert into @Cash
		SELECT dbo.docinfo_temp.DI_DATE as d,
				case when SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) = '7' then '1'
		     when SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) <> '7' then SUBSTRING(dbo.DOCTYPE.DT_BRANCH, 2, 1) end AS BranCh
		,dbo.docinfo_temp.DI_ACTIVE, SUBSTRING(dbo.SKUMASTER.SKU_CODE, 1, 10) AS itemcode,
						  CASE WHEN dbo.DOCTYPE.DT_PROPERTIES = '337' THEN (skumove.skm_Qty) * - 1 WHEN dbo.DOCTYPE.DT_PROPERTIES = '302' THEN (skumove.skm_Qty)
						  * - 1 END AS amt
		FROM     dbo.DOCTYPE INNER JOIN
						  dbo.docinfo_temp ON dbo.DOCTYPE.DT_KEY = dbo.docinfo_temp.DI_DT INNER JOIN
						  dbo.TRANSTKH ON dbo.docinfo_temp.DI_KEY = dbo.TRANSTKH.TRH_DI INNER JOIN
						  dbo.SKUMOVE ON dbo.docinfo_temp.DI_KEY = dbo.SKUMOVE.SKM_DI INNER JOIN
						  dbo.SKUMASTER ON dbo.SKUMOVE.SKM_SKU = dbo.SKUMASTER.SKU_KEY
		WHERE  /*DATEDIFF(DAY, dbo.docinfo_temp.DI_DATE, GETDATE()) < 200 and */(dbo.docinfo_temp.DI_ACTIVE = '0') And dbo.docinfo_temp.DI_REF  not like 'P%' AND (dbo.DOCTYPE.DT_PROPERTIES IN ('302', '337'))
						AND (dbo.SKUMASTER.SKU_KEY IN
							  (SELECT SKUMASTER_1.SKU_KEY
							   FROM      dbo.docinfo_temp AS DOCINFO_1 INNER JOIN
												 dbo.TRANSTKH AS TRANSTKH_1 ON DOCINFO_1.DI_KEY = TRANSTKH_1.TRH_DI INNER JOIN
												 dbo.TRANSTKD ON TRANSTKH_1.TRH_KEY = dbo.TRANSTKD.TRD_TRH INNER JOIN
												 dbo.SKUMASTER AS SKUMASTER_1 ON dbo.TRANSTKD.TRD_SKU = SKUMASTER_1.SKU_KEY))
		--====================================================

--	declare @Cash12 table (itemcode nvarchar(16),lastBuy1date date, Br nvarchar(2),Sumqty int)
	declare @Cash12 table (itemcode nvarchar(16),lastBuyTrans1date date, Br nvarchar(2),Sumqty int)
	declare @Cash34 table (itemcode nvarchar(16),lastBuyTrans1date date, Br nvarchar(2),Sumqty int)

		insert into @Cash12
				SELECT  code,Percent_Stock_Report_New.lastBuyTrans1date ,Br,sum(amt) as Sumqty
					FROM    Percent_Stock_Report_New left join @Cash as t2
					on Percent_Stock_Report_New.code = t2.itemcode and Percent_Stock_Report_New.br = t2.branch
				WHERE	Percent_Stock_Report_New.lastBuyTrans1date  is not null and pr like @Pr and d >= Percent_Stock_Report_New.lastBuyTrans1date  and Br in ('1','2')
				group by  code,Percent_Stock_Report_New.lastBuyTrans1date, br


		UPDATE Percent_Stock_Report_New
			set sumsoldcash = t2.Sumqty
			from Percent_Stock_Report_New as t1 join @Cash12 as t2
			on t1.br = t2.br and t1.code = t2.itemcode

	-- สาขา 3,4 ------------------------------------------------
		insert into @Cash34
				SELECT  code,Percent_Stock_Report_New.lastBuyTrans1date ,Br,sum(amt) as Sumqty
					FROM    Percent_Stock_Report_New left join @Cash as t2
					on Percent_Stock_Report_New.code = t2.itemcode and Percent_Stock_Report_New.br = t2.branch
				WHERE	Percent_Stock_Report_New.lastBuyTrans1date  is not null and pr like @Pr and d >= Percent_Stock_Report_New.lastBuyTrans1date  and Br in ('3','4')
				group by  code,Percent_Stock_Report_New.lastBuyTrans1date, br


		UPDATE Percent_Stock_Report_New
			set sumsoldcash = t2.Sumqty
			from Percent_Stock_Report_New as t1 join @Cash34 as t2
			on t1.br = t2.br and t1.code = t2.itemcode


	RETURN
END





