								from docinfo inner join transtkh
								on di_key=trh_di inner join transtkd
								on trh_key=TRD_TRH inner join skumaster
								on trd_sku = sku_key inner join BRANCH
								on trh_br=BR_key inner join WARELOCATION
								on trd_wl=WL_key
								where di_ref=@pr)Q2
								on Ic_temp=sku_code
								full outer join @docpo
								on Q2.SKU_CODE=codepo
								full outer join
								(select di_ref as doc_vcn,
								di_date as date_vcn,di_cre_by as cre_vcn,
								trd_qty ,sku_code,TRD_REFER_REF as trdref
								from docinfo inner join transtkh
								on di_key=trh_di inner join transtkd
								on trh_key=trd_trh inner join SKUMASTER
								on trd_sku=sku_key
								where TRD_REFER_REF in (select docpo from @docpo)) Q4
								on docpo=Q4.trdref and codepo=Q4.sku_code
								full outer join
								(select sku_code,trd_qty from docinfo inner join transtkh
								 on di_key=trh_di inner join transtkd
								 on trh_key=trd_trh inner join SKUMASTER
								 on TRD_SKU=sku_key
								 where di_ref=@pr) Q5
								 on Ic_temp=Q5.SKU_CODE
								 full outer join
								 (select ic_temp,
								           SUM(CASE WHEN EOMONTH(date_temp) = EOMONTH(getdate()) THEN qty_temp ELSE 0 ENd)
										 + SUM(CASE WHEN EOMONTH(date_temp) = EOMONTH(getdate(),-1) THEN qty_temp ELSE 0 ENd)
										 + SUM(CASE WHEN EOMONTH(date_temp) = EOMONTH(getdate(),-2) THEN qty_temp ELSE 0 ENd)
