										 + SUM(CASE WHEN EOMONTH(date_temp) = EOMONTH(getdate(),-4)  THEN qty_temp ELSE 0 ENd)
										 + SUM(CASE WHEN EOMONTH(date_temp) = EOMONTH(getdate(),-5) THEN qty_temp ELSE 0 ENd) as sumallbranch
										 from @temp where dt_temp in ('ขายสด','ขายเชื่อ','โอนย้าย','ซื้อ')
										 group by Ic_temp) Q6
										 on Q5.SKU_CODE=Q6.ic_temp
								where Q1.Ic_temp is not null
								order by Q1.Ic_temp,Br_temp

end

