			begin
				update transtkh set trh_br =
			case
				when DT_BRANCH='01' then 1
				when DT_BRANCH='02' then 101
				when DT_BRANCH='03' then 105
				when DT_BRANCH='04' then 103
				when DT_BRANCH='05' then 104
				when DT_BRANCH='06' then 106
				when DT_BRANCH='07' then 107
				when DT_BRANCH='08' then 108
				else trh_br
			end
