					inner join transtkh on docinfo.di_key= transtkh.trh_di
					where docinfo.di_date= @stime and DT_PROPERTIES in ('302'/*ขายสด*/,'337'/*คืนสด*/,'307'/*เครดิต*/,'308'/*คืนเครดิต*/,'311'/*โอน*/,'303'/*ซื้อ*/,'304'/*คืนซื้อ*/)
